CREATE TABLE `parking_slot_detail` ( `slot_id` INT(9) NOT NULL AUTO_INCREMENT , `slot_name` VARCHAR(255) NOT NULL , `slot_capacity` VARCHAR(255) NOT NULL , `slot_charge` VARCHAR(255) NOT NULL , `slot_manager_name` VARCHAR(255) NOT NULL , `slot_manager_contact` VARCHAR(255) NOT NULL ,`slot_manager_email` VARCHAR(255) NOT NULL , `slot_longitude` VARCHAR(255) NOT NULL , `slot_latitude` VARCHAR(255) NOT NULL , `slot_address` VARCHAR(255) NOT NULL , `slot_table` VARCHAR(255) NOT NULL , `datetime` TIMESTAMP NOT NULL , PRIMARY KEY (`slot_id`)) ENGINE = InnoDB;